mapper.update:
 		update comments 
		set detail=#{detail}
		where num_member=#{num_member}
		and num_episode=#{num_episode}

mapper.select:
 		
 		select num_member, num_episode, detail, make 
		from comments 
		where num_episode=#{num_episode}

 		select comment_num, num_member, num_episode, detail, make 
		from comments 
		where num_episode=#{num_episode}
		 and num_member=#{num_member}
